this.actor.data.items = this.actor.data.items.filter(i => {
   if (i.type == "talent")
   {
      i.data.tests.value = ""
      i.effects = []
   }
   if (i.type == "skill") 
   {
      i.data.advances.value = 0
      if (i.data.advanced.value == "adv")
         return false
   }
   return true
})

if (args.actor.isOwner)
args.actor.setupSkill("Résistance", {context : {failure: "1 point de Corruption", success : "1 Point de Chance gagné"}}).then(setupData => {
args.actor.basicTest(setupData).then(test => 
{
if (test.result.outcome == "success" && args.actor.data.type == "character")
{
args.actor.update({"data.status.fortune.value" : args.actor.status.fortune.value + 1})
}
else if (test.result.outcome == "failure" && args.actor.data.type == "character")
{
args.actor.update({"data.status.corruption.value" : args.actor.status.corruption.value + 1})
}
})
})

